import React from "react";
import classes from "./featuredProducts.module.css";
import mensImage from "../../assets/img/mensCollectionCover.jpg";

// Fisher-Yates shuffle
const shuffleArray = (array) => {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
};

function FeaturedProducts({ featuredProduct }) {
  const featured = shuffleArray(featuredProduct).slice(0, 5);

  return (
    <div className={classes.featuredGrid}>
      {featured.map((product) => (
        <div key={product.product_id} className={classes.card}>
          <img src={mensImage} alt={product.name} className={classes.image} />
          <div className={classes.info}>
            <h3 className={classes.name}>{product.name}</h3>
            <p className={classes.price}>${product.price}</p>
            <button className={classes.cartBtn}>Add to Cart</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default FeaturedProducts;
