import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import classes from "./login.module.css";
import hidePaswword from "../../assets/img/hide_password_eye.png";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:8801/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();

    if (data.success) {
      alert("Login successful!");
      const role = data.user.role;
      if (role === "admin") navigate("/adminPage");
      else navigate("/userPage");
    } else {
      alert("Invalid email or password.");
    }
  };

  return (
    <div className={classes.loginContainer}>
      <div className={classes.loginCard}>
        <h2 className={classes.loginTitle}>Login</h2>

        <form onSubmit={handleSubmit} className={classes.loginForm}>
          <div className={classes.formGroup}>
            <label htmlFor="email" className={classes.formLabel}>
              Email
            </label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className={classes.formInput}
              placeholder="Enter your email"
            />
          </div>

          <div className={classes.formGroup}>
            <label htmlFor="password" className={classes.formLabel}>
              Password
            </label>
            <div className={classes.passwordContainer}>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className={classes.formInput}
                placeholder="Enter your password"
              />
              <div className={classes.passwordToggle}>
                <img src={hidePassword} alt="Toggle password visibility" />
              </div>
            </div>
          </div>

          <div className={classes.formActions}>
            <button type="submit" className={classes.loginButton}>
              Login
            </button>
            <button
              type="button"
              className={classes.forgotButton}
              onClick={() => alert("Password reset functionality coming soon!")}
            >
              Forgot Password?
            </button>
          </div>
        </form>
        <p>
          {`"email": "alice@example.com" "password":"alice123"
          ------------------
          "email":"bob@example.com","password":"bob123" 
          ------------------
          "email":"carol@example.com","password": "carol123"
          ------------------
           "email": "dave@example.com","password": "dave123"`}
        </p>
      </div>
    </div>
  );
}

export default Login;
