import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import classes from "./login.module.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:8801/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();

    if (data.success) {
      alert("Login successful!");
      // Optional: save user to localStorage or redirect
      // localStorage.setItem("user", JSON.stringify(data.user));
      navigate("/adminPage");
    } else {
      alert(data.message || "Invalid email or password.");
    }
  };

  return (
    <div className={classes.loginContainer}>
      <div className={classes.loginCard}>
        <h2 className={classes.loginTitle}>Login</h2>

        <form onSubmit={handleSubmit} className={classes.loginForm}>
          <div className={classes.formGroup}>
            <label htmlFor="email" className={classes.formLabel}>
              Email
            </label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className={classes.formInput}
              placeholder="Enter your email"
            />
          </div>

          <div className={classes.formGroup}>
            <label htmlFor="password" className={classes.formLabel}>
              Password
            </label>
            <input
              id="password"
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className={classes.formInput}
              placeholder="Enter your password"
            />
          </div>

          <div className={classes.formActions}>
            <button type="submit" className={classes.loginButton}>
              Login
            </button>
            <button
              type="button"
              className={classes.forgotButton}
              onClick={() => alert("Password reset functionality coming soon!")}
            >
              Forgot Password?
            </button>
          </div>
        </form>
        <p>
          "email": "alice@example.com", "name": "Alice Green", "password":
          "alice123", "address": "123 Maple Street", "role": "admin", "phone":
          "0501234567" --------------------------------- "email":
          "bob@example.com", "name": "Bob Blue", "password": "bob123",
          "address": "456 Oak Avenue", "role": "user", "phone": "0527654321"
          -------------------------------- "email": "carol@example.com", "name":
          "Carol Red", "password": "carol123", "address": "789 Pine Road",
          "role": "user", "phone": "0539876543" -------------------------------
          "email": "dave@example.com", "name": "Dave White", "password":
          "dave123", "address": "321 Cedar Blvd", "role": "moderator", "phone":
          "0543219876"
        </p>
      </div>
    </div>
  );
}

export default Login;
