import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../../assets/styles/MainPage.css";
import axios from "axios";

function MainPage() {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    fetchData();
  }, []);

  const [msg, setMsg] = useState("");

  const fetchData = () => {
    axios
      .get("user")
      .then((res) => {
        setUsers(res.data);
        console.log(res.data); // Data from API
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const handleDelete = (post) => {
    if (window.confirm(`Are you sure you want to delete post: ${post.name}`)) {
      axios
        .delete(`/users/${post.email}`)
        .then((res) => {
          console.log("Post deleted:", res.data); // Article deleted
          // After deletion, update the list of articles
          setUsers(users.filter((user) => user.id !== post.id));
          setMsg("user was deleted");
          // Clear the message after 1 second
          setTimeout(() => {
            setMsg(""); // Clear the message after 1 second
          }, 2000); // 1000 ms = 1 second
        })
        .catch((error) => {
          console.error("Error:", error);
        });
    }
  };
  return (
    <div className="main">
      <section className="articles">
        <div className="container">
          <h1 className="main-page-title">Users</h1>
          <div className="msg">{msg}</div>

          <div className="articles-container">
            {users &&
              users.map((user) => (
                <div key={user.email} className="article-card">
                  <h2 className="article-title">{user.name}</h2>
                  <div className="d-flex g-1">
                    <Link to={`/post/${user.email}`} className="view-button">
                      View
                    </Link>
                    <button
                      className="view-button mt1"
                      onClick={() => handleDelete(user)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </section>
    </div>
  );
}

export default MainPage;
