//     const j = Math.floor(Math.random() * (i + 1));
//     [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
//   }
//   return shuffled;
// };

function ProductShow({ product }) {
  return (
    <div className={classes.featuredGrid}>
      <div key={product.product_id} className={classes.card}>
        <img src={mensImage} alt={product.name} className={classes.image} />
        <div className={classes.info}>
          <h3 className={classes.name}>{product.name}</h3>
          <p className={classes.price}>${product.price}</p>
          <button className={classes.cartBtn}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductShow;
