import React from "react";
import classes from "./productShow.module.css";
import mensImage from "../../assets/img/mensCollectionCover.jpg";
import { useNavigate } from "react-router-dom";

function ProductShow({ product }) {
  const navigate = useNavigate();
  
  // Log the product to see its structure
  console.log("Product in ProductShow:", product);
  
  const handleProductClick = () => {
    console.log("Navigating to product with ID:", product.product_id);
    navigate(`/products/${product.product_id}`);
  };
  
  return (
    <div className={classes.featuredGrid}>
      <div key={product.product_id} className={classes.card}>
        <img
          onClick={handleProductClick}
          src={mensImage}
          alt={product.name}
          className={classes.image}
        />
        <div className={classes.info}>
          <h3 className={classes.name}>{product.name}</h3>
          <p className={classes.price}>${product.price}</p>
          <button className={classes.cartBtn}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductShow;
