  //         }, 2000); // 1000 ms = 1 second
  //       })
  //       .catch((error) => {
  //         console.error("Error:", error);
  //       });
  //   }
  // };
  return (
    <div className={classes.main}>
      <section className={classes.section1}>
        <h1>New Season</h1>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Expedita,
          beatae.
        </p>
        <div className={classes.btns}>
          <button onClick={() => navigate("/men")}>Shop Men</button>
          <button onClick={() => navigate("/women")}>Shop Women</button>
        </div>
      </section>
      <section className={classes.section2}>
        <h1>Featured Products</h1>
        <div className={classes.FeaturedProducts}>
          {products.map((product) => (
            <ProductShow key={crypto.randomUUID()} product={product} />
          ))}
        </div>
      </section>
      <section className={classes.section3}>
        <img src={womensImage} alt="" />
        <div>
          <h2>Women's collection</h2>
          <p>
            Lorem, ipsum dolor sit amet consectetur adipisicing elit. Eligendi,
            totam.
          </p>
        </div>
      </section>
      <section className={classes.section4}>
        <div>
          <h2>Men's collection</h2>
          <p>
            Lorem, ipsum dolor sit amet consectetur adipisicing elit. Eligendi,
            totam.
          </p>
        </div>
        <img src={mensImage} alt="" />
      </section>
    </div>
  );
}

export default MainPage;
